import { IEdcPopoverOptions } from './edc-popover-options.interface';

/**
 * Popover global configuration, to provide when importing the edc help module
 *
 *    pluginId: URL to the help web app.
 *    helpPath: URL to the HTTP served export.
 *    docPath: Export plugin name for the edc documentation.
 *    i18nDirName: Path to the i18n files, containing the labels
 *    options: Popover global options, to apply to all popovers
 *
 */
export interface EdcPopoverConfiguration {
  pluginId: string;
  helpPath: string;
  docPath: string;
  i18nDirName?: string;
  options?: IEdcPopoverOptions;
}

import { ModuleWithProviders, NgModule, Provider } from '@angular/core';
import { CommonModule } from '@angular/common';
import { HelpComponent } from './help.component';
import { HelpService } from './services/help.service';
import { EdcTranslationService } from './services/edc-translation.service';
import { HelpPopoverDirective } from './help-popover.directive';
import { HelpConfigService } from './services/help-config.service';
import { HelpIconService } from './services/help-icon.service';
import { HelpErrorService } from './services/help-error.service';
import { HelpPopoverService } from './services/help-popover.service';

export interface HelpModuleConfig {
  configLoader: Provider;
}

@NgModule({
  imports: [
    CommonModule
  ],
  declarations: [
    HelpComponent,
    HelpPopoverDirective
  ],
  providers: [
    HelpService,
    EdcTranslationService,
    HelpConfigService,
    HelpIconService,
    HelpErrorService,
    HelpPopoverService
  ],
  exports: [
    HelpComponent
  ],
  entryComponents: [
    HelpComponent
  ]
})
export class HelpModule {
  static forRoot(config: HelpModuleConfig): ModuleWithProviders<HelpModule> {
    return {
      ngModule: HelpModule,
      providers: [
        HelpService,
        config.configLoader
      ]
    };
  }
}

import { IPopoverOptions } from 'edc-popover-utils';
import { FailBehavior } from './fail-behavior';
import { PopoverIcon } from './popover-icon';

export interface IEdcPopoverOptions extends IPopoverOptions {
  icon?: PopoverIcon;
  dark?: boolean;
  failBehavior?: FailBehavior;
}

