import { Injectable } from '@angular/core';
import { EdcConfiguration } from 'src/config/edc-configuration';
import { HttpClient } from '@angular/common/http';

@Injectable()
export class ConfigService {
  private config: EdcConfiguration;

  constructor(private http: HttpClient) {
  }

  load(url: string): Promise<EdcConfiguration> {
    return new Promise((resolve) => {
      this.http.get(url).subscribe((config: EdcConfiguration) => {
          this.config = config;
          resolve();
        });
    });
  }

  getConfiguration(): EdcConfiguration {
    return this.config;
  }

}

import { Injectable } from '@angular/core';
import { SYS_LANG } from '../translate/language-codes';
import { HelpService } from './help.service';
import { PopoverLabel } from 'edc-client-js';
import { DEFAULT_LABELS } from '../translate/default-translations';

@Injectable()
export class EdcTranslationService {

  lang = SYS_LANG;

  constructor(private readonly helpService: HelpService) {
  }

  getLang(): string {
    return this.lang;
  }

  setLang(lang: string): void {
    this.lang = lang;
  }

  /**
   * Returns the popover labels from the i18n files in the publication export
   *
   * If an error occurred or no translations were returned, returns default labels
   *
   * @param lang the language to use
   */
  getPopoverLabels(lang: string = this.lang): Promise<PopoverLabel> {
    const langToUse = this.helpService.isLanguagePresent(lang) ? lang : SYS_LANG;
    return this.helpService.getPopoverLabels(langToUse)
      .then((translations: PopoverLabel) => translations || this.loadDefaultLabels(lang))
      .catch(() => this.loadDefaultLabels(lang));
  }

  /**
   * Load default popover labels on error
   *
   * @param lang the lang code
   */
  loadDefaultLabels(lang: string): Promise<PopoverLabel> {
    const labelTranslation = DEFAULT_LABELS.get(lang) || DEFAULT_LABELS.get(SYS_LANG);

    return Promise.resolve(labelTranslation);
  }
}

import { Injectable } from '@angular/core';
import { HelpService } from './help.service';
import { Helper } from 'edc-client-js';
import { IEdcPopoverOptions } from '../config/edc-popover-options.interface';
import { EdcPopoverOptions } from '../config/edc-popover-options';
import { copyDefinedProperties } from '../utils/global.utils';
import { IconPopoverConfig } from '../config/icon-popover-config';
import { HelpIconService } from './help-icon.service';
import { IconConfig } from '../config/icon-config';
import { HelpPopoverService } from './help-popover.service';
import { HelpErrorService } from './help-error.service';

@Injectable()
export class HelpConfigService {

  constructor(private readonly helpService: HelpService,
              private readonly helpIconService: HelpIconService,
              private readonly helpPopoverService: HelpPopoverService,
              private readonly helpErrorService: HelpErrorService) {
  }

  /**
   * Creates the popover configuration to generate the icon and the popover elements
   *
   * Relies on the edc-client-js to retrieve the help content and the labels from the export
   * Adds the given options (placement, style, behavior...)
   * Prepares the icon configuration
   *
   * If an error occurs, generates an error configuration for a icon / popover,
   * based on the fail behavior options.
   *
   * @param mainKey the brick primary key
   * @param subKey the brick sub key
   * @param pluginId the plugin Identifier
   * @param lang the lang to use
   * @param options the options for this popover
   */
  buildPopoverConfig(mainKey: string,
                     subKey: string,
                     pluginId?: string,
                     lang?: string,
                     options?: IEdcPopoverOptions): Promise<void | IconPopoverConfig> {
    // Get the helper from edc-client-js
    return this.helpService.getHelp(mainKey, subKey, pluginId, lang)
      .then((helper: Helper) => this.helpPopoverService.addContent(helper, mainKey, subKey, lang))
      .then((config: IconPopoverConfig) => this.addOptions(config, options))
      .then((config: IconPopoverConfig) => this.helpPopoverService.addLabels(config))
      .then((config: IconPopoverConfig) => this.addIconToConfig(config))
      .catch((err: Error) => this.handleError(err, options, lang));
  }

  /**
   * Returns the classes to use for the popover icon
   *
   * @param config the popover configuration
   */
  getIconClasses(config: IconPopoverConfig): string | string[] {
    const iconConfig: IconConfig = config && config.iconConfig;
    return this.helpIconService.getIconClasses(iconConfig);
  }

  /**
   * Merges and saves options in the configuration respecting priorities from the different sources.
   * In order - higher levels will overwrite the lower ones :
   *    Default options < global options < single popover input options
   *
   * @param config the popover configuration
   * @param options the options for this popover
   */
  addOptions(config: IconPopoverConfig, options: IEdcPopoverOptions): IconPopoverConfig {
    // Update configuration with resolved options
    config.options = this.resolveOptions(options);
    return config;
  }

  /**
   * Intercepts help errors, from the client-js or from a content error
   *
   * Resolves options, and sends the error to the helpErrorService
   *
   * @param err the error
   * @param options the options initially passed to the popover
   * @param lang the language to use, for the labels
   * @private
   */
  private handleError(err: Error, options: IEdcPopoverOptions, lang: string): Promise<IconPopoverConfig> {
    // Process options before handing them to the error service
    const resolvedOptions = this.resolveOptions(options);
    return this.helpErrorService.handleHelpError(err, resolvedOptions, lang);
  }

  /**
   * Resolves the options to use, mixing the 3 levels:
   *      - Populate with default options
   *      - Overwrites with the global options properties, if defined
   *      - Overwrites with popover level options, passed to this particular popover instance
   *
   * Skip any undefined property
   *
   * @param options the Popover options
   * @private
   */
  private resolveOptions(options: IEdcPopoverOptions): IEdcPopoverOptions {
    // Start merging global options into default ones to make sure all required values are defined
    const globalOptions: IEdcPopoverOptions = copyDefinedProperties<IEdcPopoverOptions>(
      new EdcPopoverOptions(),
      this.helpService.getPopoverOptions()
    );
    // Then override with more specific options from the popover component and set it as config options
    return copyDefinedProperties<IEdcPopoverOptions>(globalOptions, options);
  }

  /**
   * Builds the icon configuration from the given options
   *
   * @param config the popover configuration
   * @private
   */
  private addIconToConfig(config: IconPopoverConfig): IconPopoverConfig {
    config.iconConfig = this.helpIconService.buildIconConfig(config.options, config.labels);

    return config;
  }
}

import { Injectable } from '@angular/core';
import { PopoverContent } from 'edc-popover-utils';
import { FailBehavior, IconBehavior, PopoverBehavior } from '../config/fail-behavior';
import { ContentNotFoundError } from '../errors/content-not-found.error';
import { IconPopoverConfig } from '../config/icon-popover-config';
import { HelpIconService } from './help-icon.service';
import { IEdcPopoverOptions } from '../config/edc-popover-options.interface';
import { PopoverIcon } from '../config/popover-icon';
import { PopoverLabel } from 'edc-client-js';
import { HelpPopoverService } from './help-popover.service';

@Injectable()
export class HelpErrorService {

  constructor(private readonly helpIconService: HelpIconService,
              private readonly helpPopoverService: HelpPopoverService) {
  }

  /**
   * Handles errors and build a popover containing the error messages
   *
   * @param err the error
   * @param options the options that were passed to the popover initially
   * @param lang the lang to use
   */
  handleHelpError(err: Error, options: IEdcPopoverOptions, lang: string): Promise<IconPopoverConfig> {
    // If error came when retrieving the labels, just use default and continue with loaded content
    if (err instanceof ContentNotFoundError) {
      return this.handleContentException(options, lang);
    }
    return this.handleOtherException(options);
  }

  /**
   * Handles errors of type content error
   * Creates the error popover, with labels and error
   *
   * @param options the options that were passed to the popover
   * @param lang the lang to use for the labels
   * @private
   */
  private handleContentException(options: IEdcPopoverOptions, lang?: string): Promise<IconPopoverConfig> {
    if (!options.failBehavior) {
      options.failBehavior = new FailBehavior();
    }
    if (!options.icon) {
      options.icon = PopoverIcon.create();
    }

    const errorConfig: IconPopoverConfig = new IconPopoverConfig();
    errorConfig.options = options;

    // Retrieve the labels and then create the error popover configuration
    return this.helpPopoverService.addLabels(errorConfig, lang)
      .then((config: IconPopoverConfig) => this.buildErrorConfig(config));
  }

  /**
   * Handles exceptions other than content exceptions
   *
   * @param options the options that were passed to the popover
   * @private
   */
  private handleOtherException(options: IEdcPopoverOptions): Promise<IconPopoverConfig> {
    // Use content exception handling as default for now
    return this.handleContentException(options);
  }

  /**
   * Builds the configuration object for the error popover
   *
   * @param config the configuration being created
   * @private
   */
  private buildErrorConfig(config: IconPopoverConfig): IconPopoverConfig {
    const options: IEdcPopoverOptions = config.options;
    config.iconConfig = this.helpIconService.buildErrorIconConfig(options, config.labels);

    // Popover content
    config.content = this.buildErrorContent(config.labels, options.failBehavior);
    if (!config.content) {
      config.disablePopover = true;
    }

    return config;
  }

  /**
   * Builds the content for the error popover
   * Based on fail behavior icon and popover options
   *
   * @param labels the popover labels
   * @param failBehavior the behavior options in case of error
   * @private
   */
  private buildErrorContent(labels: PopoverLabel, failBehavior: FailBehavior): PopoverContent {
    let content = new PopoverContent();
    // Icon behavior options - Hidden and Disabled don't need content
    if (failBehavior.icon === IconBehavior.HIDDEN || failBehavior.icon === IconBehavior.DISABLED) {
      return null;
    }
    // Popover behavior
    switch (failBehavior.popover) {
      case PopoverBehavior.NO_POPOVER:
        content = null;
        break;
      case PopoverBehavior.ERROR_SHOWN:
        content.title = labels.errorTitle;
        content.description = labels.errors.failedData;
        break;
      case PopoverBehavior.FRIENDLY_MSG:
        content.description = labels.comingSoon;
    }
    return content;
  }

}

import { Injectable } from '@angular/core';
import { IEdcPopoverOptions } from '../config/edc-popover-options.interface';
import { PopoverLabels } from 'edc-popover-utils';
import { PopoverIcon } from '../config/popover-icon';
import {
  DARK_CLASS_NAME, DEFAULT_ICON,
  ERROR_ICON,
  IconClass,
  IMAGE_BACKGROUND_SIZE,
  IMAGE_CLASS_NAME,
  IMAGE_HEIGHT,
  IMAGE_WIDTH
} from '../constants/style.constant';
import { IconBehavior } from '../config/fail-behavior';
import { IconConfig } from '../config/icon-config';
import { copyDefinedProperties } from '../utils/global.utils';
import { PopoverLabel } from 'edc-client-js';

@Injectable()
export class HelpIconService {

  /**
   * Builds the configuration for the popover icon, based on resolved options
   *
   * Generates:
   *      - icon: resolved icon properties from the options
   *      - iconTooltip: the icon tooltip
   *      - iconClasses: the icon main classes
   *
   * @param options the popover options from the received input
   * @param labels the popover labels
   */
  buildIconConfig(options: IEdcPopoverOptions, labels: PopoverLabel): IconConfig {
    const iconConfig: IconConfig = new IconConfig();
    // Get safe icon properties
    iconConfig.icon = this.buildPopoverIcon(options);
    // Add icon tooltip if displayTooltip is true
    iconConfig.iconTooltip = this.getTooltip(options && options.displayTooltip, labels);
    // Create the icon classes that will be used in the component
    iconConfig.iconClasses = this.buildIconClasses(iconConfig.icon, options);
    // Check for any provided url
    if (iconConfig.icon.url) {
      // Update icon config with the css style declaration
      iconConfig.imageStyle = this.getIconImageStyle(iconConfig.icon);
    }
    return iconConfig;
  }

  /**
   * Returns the tooltip for the icon, on hover
   * Uses the 'iconAlt' property from the translated popover labels,
   * provided by the the edc-client-js instance
   *
   * returns an empty string if displayTooltip option is false,
   * or if labels were not found
   *
   * @param displayTooltip true if tooltip should be displayed (from the popover options)
   * @param labels the translated labels containing the text for the tooltip
   */
  getTooltip(displayTooltip: boolean, labels: PopoverLabels): string {
    return (displayTooltip && !!labels) ? labels.iconAlt : '';
  }

  /**
   * Returns an array containing the class names for the icon,
   * to pass to the angular ngClass directive
   *
   * Looks for loaded classes in the icon configuration object
   * Adds the error classes if this is a error popover
   *
   * If no configuration was found, return an empty string
   *
   * @param iconConfig the icon configuration
   */
  getIconClasses(iconConfig: IconConfig): string | string[] {
    if (!iconConfig) {
      return '';
    }
    const classes = [];
    if (iconConfig.iconClasses && iconConfig.iconClasses.length) {
      classes.push(...iconConfig.iconClasses);
    }
    if (iconConfig.errorClasses && iconConfig.errorClasses.length) {
      classes.push(...iconConfig.errorClasses);
    }
    return classes;
  }

  /**
   * Builds the icon classes from the popover options and icon properties
   *
   * Icon can be set either from a css class (ie font-awesome: 'fa fa-question-circle-o'),
   * or from an url.
   * First checks if url is provided, and if it is use it to define the icon
   * Only uses class if no url was found. That means that if both were provided,
   * only the url will be used.
   *
   * Will also set the dark mode adding it to the class list if dark option is true
   *
   * @param icon the given icon
   * @param options the popover options object, containing the options for the icon
   */
  buildIconClasses(icon: PopoverIcon, options: IEdcPopoverOptions): string[] {
    const classes = [];
    // Set dark class
    if (options && options.dark) {
      classes.push(DARK_CLASS_NAME);
    }
    // First check if url is present, and set icon as image if it is
    if (icon && icon.url) {
      classes.push(IMAGE_CLASS_NAME);
    } else if (icon.class) {
      // Only use class for defining the icon if an url was not provided
      classes.push(icon.class);
    }
    return classes;
  }

  /**
   * Returns the css style declaration for the given icon image:
   *    - Sets the background image from url
   *    - Sets the width and height from the icon properties
   *      If width is not defined, uses height
   *      Uses Default values for properties if not defined
   *
   * @param icon the icon image to style
   */
  getIconImageStyle(icon: PopoverIcon): Partial<CSSStyleDeclaration> {
    if (!icon || !icon.url) {
      return null;
    }
    // Set given image url as background, and width and height from the icon properties
    return {
      background: `url(${icon.url})`,
      backgroundSize: IMAGE_BACKGROUND_SIZE,
      height: `${icon.height || IMAGE_HEIGHT}px`,
      width: `${icon.width || icon.height || IMAGE_WIDTH}px`,
    };
  }

  /**
   * Builds a icon configuration for an error, based on the given options
   *
   * Adjusts all the configuration values associated with the given icon behavior.
   *
   * Behavior will define:
   *    icon: use the Error icon if Error behavior was set, else try to use the original icon's
   *    and if not present use default
   *
   *    icon style: - Shown: will display the normal icon
   *                - Error: will add the edc-icon-error class
   *                - Hidden: will add the edc-icon-hidden class
   *                - DISABLED: will add the edc-icon-disabled class
   *
   * @param options the popover options
   * @param labels the labels
   */
  buildErrorIconConfig(options: IEdcPopoverOptions, labels: PopoverLabel): IconConfig {
    // Build common icon config
    const { icon: iconBehavior } = options.failBehavior;
    // If behavior for the icon is to display an error icon, update its class
    if (iconBehavior === IconBehavior.ERROR) {
      options.icon.class = ERROR_ICON;
    }
    // Start building the main icon configuration
    const iconConfig = this.buildIconConfig(options, labels);
    // Define the classes associated with each behavior:
    const errorClasses: Map<IconBehavior, IconClass> = new Map()
      .set(IconBehavior.SHOWN, IconClass.NONE)
      .set(IconBehavior.DISABLED, IconClass.DISABLED)
      .set(IconBehavior.HIDDEN, IconClass.HIDDEN)
      .set(IconBehavior.ERROR, IconClass.ERROR);
    // Any specific error class to be added, according to the defined behavior
    const errorClass = errorClasses.get(iconBehavior);
    iconConfig.errorClasses = errorClass ? [errorClass] : [];
    return iconConfig;
  }

  /**
   * Builds the icon options
   *
   * Checks if at least one of the class or url properties should be defined
   * If not, use default icon class
   *
   * returns a safe PopoverIcon
   * @param options the popover options
   * @private
   */
  private buildPopoverIcon(options: IEdcPopoverOptions): PopoverIcon {
    const defaultIcon = PopoverIcon.create();
    if (!options || !options.icon) {
      return defaultIcon;
    }
    // Make a safe copy from default icon values
    const mergedIcons = copyDefinedProperties<PopoverIcon>(defaultIcon, options.icon);
    // Check consistency and clean invalid properties
    if (!mergedIcons.url || !mergedIcons.url.trim()) {
      // url property must be null or undefined if not valid
      mergedIcons.url = null;
    }
    if (!mergedIcons.class || !mergedIcons.class.trim()) {
      // At least one of the class or url properties must be defined, use default values if not
      mergedIcons.class = mergedIcons.url ? null : DEFAULT_ICON;
    }
    return mergedIcons;
  }
}

import { Injectable } from '@angular/core';
import { Article, Helper, Link, PopoverLabel } from 'edc-client-js';
import { IconPopoverConfig } from '../config/icon-popover-config';
import { ContentNotFoundError } from '../errors/content-not-found.error';
import { PopoverContent } from 'edc-popover-utils';
import { HelpService } from './help.service';
import { EdcTranslationService } from './edc-translation.service';

@Injectable()
export class HelpPopoverService {

  constructor(private readonly helpService: HelpService,
              private readonly translationService: EdcTranslationService) {
  }

  /**
   * Adds the popover content
   *
   * throws a ContentNotFoundError if helper is not defined
   *
   * @param helper the edc helper that will request the content using the edc-client-js instance
   * @param mainKey the brick primary key
   * @param subKey the brick sub key
   * @param lang the lang to use
   * @private
   */
  addContent(helper: Helper, mainKey: string, subKey: string, lang: string): IconPopoverConfig {
    const config = new IconPopoverConfig();
    if (!helper) {
      // The help client could not resolve any helper for the content, throw an error
      throw new ContentNotFoundError(mainKey, subKey, lang);
    }
    // Retrieve the language that the helper resolved, from the requested and the current export state
    const { language: resolvedLanguage } = helper;
    // Resolved language might be different from the requested, if content was not available in that language
    // Keep the language service up to date with the finally used language
    this.translationService.setLang(resolvedLanguage);
    // Extract and create the popover content
    const { label: title, description, articles, links } = helper;
    config.content = new PopoverContent(title, description, articles, links);
    // Parse articles and links urls
    this.parseUrls(config, mainKey, subKey, resolvedLanguage, helper.exportId);
    return config;
  }

  /**
   * Adds labels into the popover configuration
   *
   * @param config the popover configuration being created
   * @param lang the lang to use
   */
  addLabels(config: IconPopoverConfig, lang?: string): Promise<IconPopoverConfig> {
    return this.translationService.getPopoverLabels(lang)
      .then((translations: PopoverLabel) => {
        config.labels = translations;
        return config;
      });
  }

  /**
   * Sets the url attributes for links and articles to open in the web help explorer,
   * inserting the help path defined in the global configuration
   *
   * @param config the popover configuration
   * @param mainKey the brick primary key
   * @param subKey the brick sub key
   * @param lang the lang to use
   * @param pluginId the identifier of the published export the keys belong to
   * @private
   */
  private parseUrls(config: IconPopoverConfig, primaryKey: string, subKey: string, lang: string, pluginId?: string): void {
    if (!config || !config.content) {
      return;
    }
    const articles = config.content.articles || [];
    const links = config.content.links || [];
    articles.forEach((article: Article, index: number) =>
      article.url = this.helpService.getContextUrl(primaryKey, subKey, lang, index, pluginId));
    links.forEach((link: Link) => link.url = this.helpService.getDocumentationUrl(link.id));
  }
}

import { Injectable } from '@angular/core';
import { EdcClient, Helper, PopoverLabel } from 'edc-client-js';
import { PopoverConfigurationHandler } from '../config/popover-configuration-handler';
import { SYS_LANG } from '../translate/language-codes';
import { IEdcPopoverOptions } from '../config/edc-popover-options.interface';
import { EdcPopoverOptions } from '../config/edc-popover-options';

@Injectable()
export class HelpService {

  private readonly edcClient: EdcClient;

  constructor(private configurationHandler: PopoverConfigurationHandler) {
    this.edcClient = new EdcClient(configurationHandler.getDocPath(),
      configurationHandler.getHelpPath(),
      configurationHandler.getPluginId(),
      true, // Context only, don't load the whole doc
      configurationHandler.getI18nPath()
    );
  }

  getHelp(primaryKey: string, subKey: string, pluginId?: string, lang?: string): Promise<Helper> {
    const pluginIdentifier = pluginId || this.configurationHandler.getPluginId();
    return this.edcClient.getHelper(primaryKey, subKey, pluginIdentifier, lang);
  }

  getContextUrl(mainKey: string, subKey: string, languageCode: string, articleIndex: number, pluginId?: string): string {
    return this.edcClient.getContextWebHelpUrl(mainKey, subKey, languageCode, articleIndex, pluginId);
  }

  getDocumentationUrl(docId: number): string {
    return this.edcClient.getDocumentationWebHelpUrl(docId);
  }

  getI18nUrl(): string {
